import React from 'react';
import { connect } from 'react-redux';

import { selectCollection } from '../../redux/shop/shop.selectors';


import { CollectionPageContainer, TitleContainer, CollectionPageItemsContainer, CollectionPageItemContainer } from './collection.styles';

const CollectionPage = ({ collection }) => {
    const { title, items } = collection;
    return(
        <CollectionPageContainer>
            <TitleContainer>{ title }</TitleContainer>
            <CollectionPageItemsContainer>
                {
                    items.map(item => <CollectionPageItemContainer key={item.id} item={item}></CollectionPageItemContainer>)
                }
            </CollectionPageItemsContainer>
        </CollectionPageContainer>
    );
}

const mapStateToProps = (state, ownProps) => ({
    collection: selectCollection(ownProps.match.params.collectionId)(state)
})

export default connect(mapStateToProps)(CollectionPage);
