import React from 'react';
import { withRouter } from 'react-router-dom';

import { MenuTitleContainer, MenuSubtitleContainer } from './menu-item.styles';

const MenuItem = ({id, title, imageUrl, size, linkUrl, history, match}) =>
(
    <div        
        key={id} 
        className={`${size} menu-item`}
        onClick={ () => history.push(`${match.url}${linkUrl}`) }>
        <div className='background-image' style={{backgroundImage: `url(${imageUrl})`}} ></div>
        <div className='content'>
            <MenuTitleContainer>{title.toUpperCase()}</MenuTitleContainer>
            <MenuSubtitleContainer>SHOP NOW</MenuSubtitleContainer>
        </div>
    </div>
);

export default withRouter(MenuItem);
